                                            {month}
                                        </option>
                                    ))}
                                </select>
                                <select
                                    className="leftPosition2"
                                    name="year"
                                    ref={register({ required: true })}
                                >
                                    {year.reverse().map((year) => (
                                        <option key={year} value={year}>
                                            {year}
                                        </option>
                                    ))}
                                </select>
                                <br />
                                <label className="rightPosition3">Gender</label>
                                <br />
                                <select
                                    className="rightPosition3"
                                    name="gender"
                                    ref={register({ required: true })}
                                >
                                    <option value="Gender"></option>
                                    <option value="female">Female</option>
                                    <option value="Male">Male</option>
                                    <option value="others">Others</option>
                                </select>
                                <br />
                                <label className="leftPosition3">Address Line 1</label>
                                <br />
                                <input
                                    className="leftPosition3"
                                    name="address"
                                    ref={register({ required: true })}
                                />
                                <br />
                                {errors.address && (
                                    <small className="leftPosition3">
                                        Address Line 1 is Required
                                    </small>
                                )}
                                <br />
                                <label className="rightPosition4">Country/Region</label>
                                <br />
                                <input
                                    className="rightPosition4"
                                    name="countryRegion"
                                    ref={register({ required: true })}
                                />
                                <br />
                                {errors.countryRegion && (
                                    <small className="rightPosition4">
                                        Country/Region is Required
                                    </small>
                                )}
                                <br />
                                <label className="leftPosition4">City</label>
                                <br />
                                <input
                                    className="leftPosition4"
                                    name="city"
                                    ref={register({ required: true })}
                                />
                                <br />
                                {errors.city && (
                                    <small className="leftPosition4">City is Required</small>
                                )}
                                <br />
                                <label className="rightPosition5">Postcode</label>
                                <br />
                                <input
                                    className="rightPosition5"
                                    name="postcode"
                                    ref={register({ required: true, pattern: /[0-9]/g })}
                                />
                                <br />
                                {errors.postcode && (
                                    <small className="rightPosition5">
                                        Insert a valid postcode
                                    </small>
                                )}
                            </form>
                        </div>
                        <div
                            className="paymentForm"
                            style={{ display: activeStep === 1 ? "flex" : "none" }}
                        >
                            <PaymentContext.Provider value={[paymentMethod, setPaymentMethod]}>
                                <Elements stripe={stripePromise} options={ELEMENTS_OPTIONS}>
                                    <Stripe />
                                </Elements>
                            </PaymentContext.Provider>
                        </div>
                        <div
                            className="finishingMessage"
                            style={{ display: activeStep === 2 ? "flex" : "none" }}
                        >
                            <div>
                                <h3>Congratulation, now you are a member of our family.</h3>
                                <img src={congratulation} alt="" />
                            </div>
                        </div>
                        <div className="steps">
                            <div>
                                {activeStep === steps.length ? (
                                    <div>
                                        <Button onClick={handleReset} className={classes.button}>
                                            Reset
                  </Button>
                                    </div>
                                ) : (
                                        <div>
                                            <div className="nextBtn">
                                                <Button
                                                    disabled={activeStep === 0}
                                                    onClick={handleBack}
                                                    className={classes.button}
                                                >
                                                    Back
                    </Button>
                                                {activeStep === 0 ? (
                                                    <Button
                                                        variant="contained"
                                                        style={{ background: "goldenrod" }}
                                                        onClick={handleSubmit((data) => {
                                                            setCustomerInformation(data);
                                                            handleNext();
                                                        })}
                                                        className={classes.button}
                                                    >
                                                        Next
                                                    </Button>
                                                ) : (
                                                        <section>
                                                            {activeStep === 1 ? (
                                                                <Button
                                                                    disabled={paymentMethod === null}
                                                                    variant="contained"
                                                                    style={{ background: "goldenrod" }}
                                                                    onClick={handleSubmit((data) => {
                                                                        setCustomerInformation(data);
                                                                        handleNext();
                                                                    })}
                                                                    className={classes.button}
                                                                >
                                                                    Next
                                                                </Button>
                                                            ) : (
                                                                    <Button
                                                                        variant="contained"
                                                                        style={{ background: "goldenrod" }}
                                                                        onClick={handleSubmit((data) => {
                                                                            handleNext();
                                                                            window.location.reload();
                                                                        })}
                                                                        className={classes.button}
                                                                    >
                                                                        finish
                                                                    </Button>
                                                                )}
                                                        </section>
                                                    )}
                                            </div>
                                        </div>
                                    )}
                            </div>
                        </div>
                    </section>
                )}
        </main>
    );
};

